import React, { useState } from "react";
import { FaStar } from "react-icons/fa";
import { useDispatch } from "react-redux";
import { addToCart } from "../../../redux/orebiSlice";

const ProductInfo = ({ productInfo }) => {
  const dispatch = useDispatch();
  const [rating, setRating] = useState(0);
  const [comment, setComment] = useState("");

  // Thêm sản phẩm vào giỏ hàng
  const handleAddToCart = () => {
    dispatch(
      addToCart({
        _id: productInfo.id,
        name: productInfo.productName,
        quantity: 1,
        image: productInfo.img,
        badge: productInfo.badge,
        price: productInfo.price,
        colors: productInfo.color,
      })
    );
  };

  // Xử lý bình luận
  const handleCommentSubmit = (e) => {
    e.preventDefault();
    console.log("New Comment: ", comment);
    setComment("");
  };

  return (
    <div className="flex flex-col gap-5">
      <h2 className="text-4xl font-semibold">{productInfo.productName}</h2>
      <p className="text-xl font-semibold">{productInfo.price} VND</p>
      <p className="text-base text-gray-600">{productInfo.des}</p>
      <p className="text-sm">Be the first to leave a review.</p>
      
      {/* Đánh giá sản phẩm */}
      <div className="flex items-center gap-2">
        <span className="text-lg font-semibold">Rating:</span>
        {[...Array(5)].map((_, index) => (
          <FaStar
            key={index}
            className={`cursor-pointer ${index < rating ? "text-yellow-400" : "text-gray-300"}`}
            onClick={() => setRating(index + 1)}
          />
        ))}
      </div>

      {/* Thêm vào giỏ hàng */}
      <button
        onClick={handleAddToCart}
        className="w-full py-4 bg-primeColor hover:bg-black duration-300 text-white text-lg font-titleFont"
      >
        Add to Cart
      </button>

      <div className="mt-6">
        <h3 className="text-2xl font-semibold">Description / Reviews</h3>
        <p className="text-base text-gray-700">{productInfo.des}</p>
      </div>

      {/* Bình luận */}
      <div className="mt-6">
        <h4 className="text-xl font-semibold">Customer Reviews</h4>
        <form onSubmit={handleCommentSubmit} className="mt-4 flex flex-col gap-4">
          <textarea
            value={comment}
            onChange={(e) => setComment(e.target.value)}
            placeholder="Leave a review..."
            rows="4"
            className="p-4 border rounded-md"
          />
          <button
            type="submit"
            className="py-2 px-4 bg-blue-500 text-white rounded-md hover:bg-blue-700"
          >
            Post Review
          </button>
        </form>

        {/* Hiển thị bình luận */}
        <div className="mt-4">
          <div className="flex items-center gap-2 mb-4">
            <img
              src="https://randomuser.me/api/portraits/men/1.jpg"
              alt="user"
              className="w-8 h-8 rounded-full"
            />
            <p className="font-semibold">John Doe</p>
            <div className="flex items-center gap-1 text-yellow-400">
              {[...Array(5)].map((_, index) => (
                <FaStar key={index} />
              ))}
            </div>
          </div>
          <p>"Lorem ipsum dolor sit amet, consectetur adipiscing elit. Quisque sit amet erat tortor."</p>
        </div>
      </div>
    </div>
  );
};

export default ProductInfo;
