// src/pages/ProductDetails/ProductDetails.js
import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { FaStar, FaRegStar } from "react-icons/fa";

const ProductDetails = () => {
  const { slug } = useParams();
  const [product, setProduct] = useState(null);
  const [qty, setQty] = useState(1);

  useEffect(() => {
    const titleFromSlug = slug
      ? slug.replace(/[-_]/g, " ").trim()
      : "Sample Product";

    setProduct({
      title: titleFromSlug,
      author: "Nguyễn Nhật Ánh",
      price: "42.000 VND",
      rating: 4,
      views: 128,
      description: [
        "Đây là đoạn mô tả mẫu cho sản phẩm.",
        "Dòng bullet thứ nhất.",
        "Dòng bullet thứ hai.",
        "Dòng bullet thứ ba.",
      ],
      images: [
        "bestSellerOne.webp",
        "bestSellerTwo.webp",
        "bestSellerThree.webp",
        "bestSellerFour.webp",
      ],
    });
  }, [slug]);

  if (!product) return <div className="p-8 text-center">Loading...</div>;

  const { title, author, price, rating, views, description, images } =
    product;

  // build star icons
  const stars = Array.from({ length: 5 }, (_, i) =>
    i < rating ? (
      <FaStar key={i} className="inline text-yellow-400" />
    ) : (
      <FaRegStar key={i} className="inline text-gray-300" />
    )
  );

  // thumbnails & main image
  const [mainImage, ...thumbs] = images;

  // helper to import images
  const importImg = (file) => {
    try {
      return require(`../../assets/images/products/bestSeller/${file}`);
    } catch {
      return "";
    }
  };

  return (
    <div className="bg-white">
      <div className="max-w-container mx-auto px-4 pt-6">
        {/* header */}
        <h1 className="text-4xl font-bold">Product</h1>
        <p className="text-sm text-gray-600 mt-1">
          /product listing/{title} – {author}
        </p>

        {/* grid 3 cols x 3 rows */}
        <div className="mt-8 grid grid-cols-1 md:grid-cols-3 md:grid-rows-3 gap-4">
          {/* three thumbnails with fixed height */}
          {thumbs.slice(0, 3).map((file, idx) => {
            const src = importImg(file);
            return (
              <img
                key={idx}
                src={src}
                alt={`thumb-${idx}`}
                className="row-start-[auto] w-full h-24 object-cover rounded-lg"
                style={{ gridRowStart: idx + 1, gridColumnStart: 1 }}
              />
            );
          })}

          {/* main image spans all 3 rows */}
          <div className="row-start-1 row-span-3 col-start-2 bg-gray-100 rounded-lg overflow-hidden flex items-center justify-center">
            {mainImage ? (
              <img
                src={importImg(mainImage)}
                alt="main"
                className="w-full h-full object-cover"
              />
            ) : (
              <div className="text-gray-500">No image</div>
            )}
          </div>

          {/* info spans all 3 rows */}
          <div className="row-start-1 row-span-3 col-start-3 flex flex-col justify-start gap-4">
            <h2 className="text-2xl font-semibold">
              {title} – {author}
            </h2>
            <div className="flex items-center space-x-2 text-sm">
              <span className="text-xl font-semibold text-red-600">
                {price}
              </span>
              <span className="text-gray-400">|</span>
              <span className="flex items-center space-x-1">{stars}</span>
              <span className="text-gray-500">| {views} view</span>
            </div>
            <p className="text-gray-700">{description[0]}</p>
            <ul className="list-disc list-inside space-y-1 text-gray-700">
              {description.slice(1).map((line, i) => (
                <li key={i}>{line}</li>
              ))}
            </ul>
            <div className="flex items-center space-x-2 mt-4">
              <button
                onClick={() => setQty((q) => Math.max(1, q - 1))}
                className="w-8 h-8 bg-black text-white flex items-center justify-center rounded"
              >
                –
              </button>
              <span className="w-8 text-center">{qty}</span>
              <button
                onClick={() => setQty((q) => q + 1)}
                className="w-8 h-8 bg-black text-white flex items-center justify-center rounded"
              >
                +
              </button>
              <button className="ml-4 bg-red-600 text-white px-6 py-2 rounded hover:bg-red-700 transition">
                Add to Cart
              </button>
            </div>
            <button className="mt-2 bg-black text-white px-6 py-2 rounded hover:bg-gray-800 transition">
              Buy Now
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductDetails;
