// src/pages/ProductDetails/ProductDetails.js
import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { FaStar, FaRegStar } from "react-icons/fa";

const ProductDetails = () => {
  const { slug } = useParams();
  const [product, setProduct] = useState(null);
  const [qty, setQty] = useState(1);

  useEffect(() => {
    const titleFromSlug = slug
      ? slug.replace(/[-_]/g, " ").trim()
      : "Có Hai Con Mèo Ngồi Bên Cửa Sổ";
    setProduct({
      title: titleFromSlug,
      author: "Nguyễn Nhật Ánh",
      price: "42.000 VND",
      rating: 5,
      views: 32,
      description: [
        "Lorem ipsum dolor sit amet consectetur adipiscing elit. Hic excepturi quibusdam odio deleniti reprehenderit facilis.",
        "Lorem ipsum dolor sit amet consectetur.",
        "Lorem ipsum dolor sit amet consectetur.",
        "Lorem ipsum dolor sit amet consectetur.",
      ],
      images: [
        "bestSellerOne.webp",
        "bestSellerTwo.webp",
        "bestSellerThree.webp",
        "bestSellerFour.webp",
      ],
    });
  }, [slug]);

  if (!product) return <div className="p-8 text-center">Loading…</div>;

  const { title, author, price, rating, views, description, images } = product;
  const [mainImage, ...thumbs] = images;

  const importImg = (file) => {
    try {
      return require(`../../assets/images/products/bestSeller/${file}`);
    } catch {
      return "";
    }
  };

  const stars = Array.from({ length: 5 }, (_, i) =>
    i < rating ? (
      <FaStar key={i} className="inline text-yellow-400" />
    ) : (
      <FaRegStar key={i} className="inline text-gray-300" />
    )
  );

  return (
    <div className="bg-white">
      <div className="max-w-container mx-auto px-4 pt-6">
        {/* Header */}
        <h1 className="text-4xl italic font-bold">Product</h1>
        <p className="text-sm text-gray-600 mt-1 mb-2">
          /product listing/{title} – {author}
        </p>

        {/* Gallery + Info */}
        <div className="grid grid-cols-1 md:grid-cols-6 md:grid-rows-3 gap-6">
          {/* Thumbnails */}
          <div className="hidden md:grid md:col-span-1 md:row-span-3 grid grid-rows-3 gap-4">
            {thumbs.slice(0, 3).map((file, idx) => (
              <img
                key={idx}
                src={importImg(file)}
                alt={`thumb-${idx}`}
                className="w-full h-32 object-cover rounded-none"
              />
            ))}
          </div>

          {/* Main image */}
          <div className="md:col-span-3 md:row-span-3 bg-gray-100 rounded-none overflow-hidden">
            {mainImage ? (
              <img
                src={importImg(mainImage)}
                alt="main"
                className="w-full h-full object-contain rounded-none"
              />
            ) : (
              <div className="text-gray-500 p-8 text-center">No image</div>
            )}
          </div>

          {/* Info & Actions */}
          <div className="md:col-span-2 md:row-span-3 flex flex-col justify-between">
            {/* Info */}
            <div>
              <h2 className="text-2xl font-semibold mb-1">
                {title} – {author}
              </h2>
              <div className="flex items-center space-x-1 text-sm mb-2">
                <span className="text-xl font-semibold text-red-600">
                  {price}
                </span>
                <span className="text-gray-400">|</span>
                <span className="flex items-center space-x-1">{stars}</span>
                <span className="text-gray-500">| {views} view</span>
              </div>
              <p className="text-gray-700 mb-1">{description[0]}</p>
              <ul className="list-disc list-inside space-y-1 text-gray-700 mb-1">
                {description.slice(1).map((line, i) => (
                  <li key={i}>{line}</li>
                ))}
              </ul>
            </div>

            {/* Actions */}
            <div className="mt-1">
              {/* Qty + Add to Cart */}
              <div className="flex items-center space-x-1 mb-1">
                <div className="inline-flex items-center h-10 bg-black text-white border border-black rounded-none">
                  <button
                    onClick={() => setQty(q => Math.max(1, q - 1))}
                    className="px-4"
                  >
                    –
                  </button>
                  <span className="px-4">{qty}</span>
                  <button
                    onClick={() => setQty(q => q + 1)}
                    className="px-4"
                  >
                    +
                  </button>
                </div>
                <button className="flex-1 h-10 bg-red-600 text-white border border-black rounded-none hover:bg-red-700 transition px-4">
                  Add to Cart
                </button>
              </div>

              {/* Buy Now */}
              <button className="w-full h-10 bg-black text-white border border-black rounded-none hover:bg-gray-800 transition px-4">
                Buy Now
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductDetails;
