// src/pages/ProductDetails/ProductDetails.js
import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { FaStar, FaRegStar } from "react-icons/fa";

const ProductDetails = () => {
  const { slug } = useParams();
  const [product, setProduct] = useState(null);
  const [qty, setQty] = useState(1);

  useEffect(() => {
    const titleFromSlug = slug?.replace(/[-_]/g, " ").trim() ?? "Sample Product";
    setProduct({
      title: titleFromSlug,
      author: "Nguyễn Nhật Ánh",
      price: "42.000 VND",
      rating: 4,
      views: 128,
      description: [
        "Đây là đoạn mô tả mẫu cho sản phẩm.",
        "Dòng bullet thứ nhất.",
        "Dòng bullet thứ hai.",
        "Dòng bullet thứ ba.",
      ],
      images: [
        "bestSellerOne.webp",
        "bestSellerTwo.webp",
        "bestSellerThree.webp",
        "bestSellerFour.webp",
      ],
    });
  }, [slug]);

  if (!product) return <div className="p-8 text-center">Loading…</div>;

  const { title, author, price, rating, views, description, images } = product;
  const [mainImage, ...thumbs] = images;

  const importImg = (f) => {
    try {
      return require(`../../assets/images/products/bestSeller/${f}`);
    } catch {
      return "";
    }
  };

  const stars = Array.from({ length: 5 }, (_, i) =>
    i < rating
      ? <FaStar key={i} className="inline text-yellow-400"/>
      : <FaRegStar key={i} className="inline text-gray-300"/>
  );

  return (
    <div className="bg-white">
      <div className="max-w-container mx-auto px-4 pt-6">
        {/* ... header & info như trước ... */}

        {/* === gallery section === */}
        <div className="mt-8 grid grid-cols-1 md:grid-cols-4 md:grid-rows-3 gap-4">
          {/* thumbnails: row-span-3 để cao = main img */}
          <div className="md:col-span-1 md:row-span-3 grid grid-rows-3 gap-4">
            {thumbs.slice(0,3).map((f,i) => {
              const src = importImg(f);
              return src && (
                <img
                  key={i}
                  src={src}
                  alt={`thumb-${i}`}
                  className="w-full h-full object-cover rounded-lg"
                />
              );
            })}
          </div>

          {/* main image: spans 3 rows, chiếm 3/4 chiều ngang */}
          <div className="md:col-span-3 md:row-span-3 bg-gray-100 rounded-lg overflow-hidden">
            {mainImage ? (
              <img
                src={importImg(mainImage)}
                alt="main"
                className="w-full h-full object-cover"
              />
            ) : (
              <div className="text-gray-500 p-8 text-center">No image</div>
            )}
          </div>
        </div>

        {/* ... phần thông tin sản phẩm phía dưới hoặc bên cạnh ... */}
      </div>
    </div>
  );
};

export default ProductDetails;
