import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import Breadcrumbs from "../../components/pageProps/Breadcrumbs";
import ProductInfo from "../../components/pageProps/productDetails/ProductInfo";

const ProductDetails = () => {
  const location = useLocation();
  const [prevLocation, setPrevLocation] = useState("");
  const [productInfo, setProductInfo] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [modalImage, setModalImage] = useState("");

  useEffect(() => {
    if (location.state && location.state.item) {
      setProductInfo(location.state.item); // Nhận dữ liệu sản phẩm từ state
    }
    setPrevLocation(location.pathname);
  }, [location]);

  const handleImageClick = (img) => {
    setModalImage(img);
    setShowModal(true); // Hiển thị modal khi bấm vào ảnh
  };

  const closeModal = () => {
    setShowModal(false); // Đóng modal
  };

  if (!productInfo) {
    return <div>Loading...</div>; // Hiển thị khi dữ liệu chưa được tải
  }

  return (
    <div className="w-full mx-auto border-b-[1px] border-b-gray-300">
      <div className="max-w-container mx-auto px-4">
        <div className="xl:-mt-10 -mt-7">
          <Breadcrumbs title="" prevLocation={prevLocation} />
        </div>
        <div className="w-full grid grid-cols-1 md:grid-cols-2 xl:grid-cols-6 gap-4 h-full -mt-5 xl:-mt-8 pb-10 bg-gray-100 p-4">
          {/* Hiển thị 4 hình ảnh */}
          <div className="h-full xl:col-span-2 grid grid-cols-2 gap-4">
            {productInfo.images?.map((img, index) => (
              <div key={index} className="cursor-pointer" onClick={() => handleImageClick(`/assets/images/products/bestSeller/${img}`)}>
                <img
                  src={`/assets/images/products/bestSeller/${img}`}
                  alt={`product-image-${index}`}
                  className="w-full h-48 object-cover rounded-lg"
                />
              </div>
            ))}
          </div>

          {/* Thông tin sản phẩm */}
          <div className="h-full w-full md:col-span-2 xl:col-span-3 xl:p-14 flex flex-col gap-6 justify-center">
            <ProductInfo productInfo={productInfo} />
          </div>
        </div>

        {/* Modal hiển thị ảnh lớn khi bấm vào ảnh nhỏ */}
        {showModal && (
          <div className="fixed inset-0 bg-gray-800 bg-opacity-75 flex items-center justify-center z-50">
            <div className="relative">
              <button
                onClick={closeModal}
                className="absolute top-0 right-0 p-4 text-white font-bold text-xl"
              >
                X
              </button>
              <img src={modalImage} alt="modal-img" className="max-w-full max-h-[80vh] object-contain" />
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default ProductDetails;
