// src/pages/ProductDetails/ProductDetails.js
import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import { FaStar, FaRegStar } from "react-icons/fa";
import Breadcrumbs from "../../components/pageProps/Breadcrumbs";

// Hàm import động ảnh từ src/assets
const importImage = (path) => {
  try {
    return require(`../../assets/images/${path}`);
  } catch {
    return "";
  }
};

const ProductDetails = () => {
  const location = useLocation();
  const [product, setProduct] = useState(null);
  const [tab, setTab] = useState("description");
  const [qty, setQty] = useState(1);

  useEffect(() => {
    if (location.state?.item) {
      setProduct(location.state.item);
    } else {
      // hardcode data demo
      setProduct({
        title: "Có Hai Con Mèo Ngồi Bên Cửa Sổ",
        author: "Nguyễn Nhật Ánh",
        price: "42.000 VND",
        rating: 4,
        views: 32,
        descriptionText: `Donec auctor, justo nec ullamcorper elementum, dui turpis efficitur ante,
nunc tempus justo lorem in lectus. Etiam viverra maximus erat, ut vulputate nisi
congue eu. Aliquam erat volutpat. Nulla facilisi. Nam at enim suscipit, fermentum
risus et, sollicitudin urna. Aenean finibus vestibulum libero, vel eleifend ante.
Curabitur gravida, orci ac scelerisque tempus, urna purus posuere est.`,
        bulletPoints: [
          "Lorem ipsum dolor sit amet consectetur.",
          "Lorem ipsum dolor sit amet consectetur.",
          "Lorem ipsum dolor sit amet consectetur.",
          "Lorem ipsum dolor sit amet consectetur.",
        ],
        images: [
          "products/bestSeller/bestSellerOne.webp",
          "products/bestSeller/bestSellerTwo.webp",
          "products/bestSeller/bestSellerThree.webp",
          "products/bestSeller/bestSellerFour.webp",
        ],
        shop: {
          name: "Shopt thú cưng",
          verified: true,
          followers: 10,
          following: 0,
        },
      });
    }
  }, [location]);

  if (!product) return null;

  const stars = [];
  for (let i = 1; i <= 5; i++) {
    stars.push(
      i <= product.rating ? (
        <FaStar key={i} className="inline text-yellow-400" />
      ) : (
        <FaRegStar key={i} className="inline text-gray-300" />
      )
    );
  }

  return (
    <div className="bg-white">
      <div className="max-w-container mx-auto px-4 pt-6">
        {/* Breadcrumb */}
        <Breadcrumbs
          title="Product"
          prevLocation={location.pathname.replace(/^\//, "")}
        />

        {/* Main grid */}
        <div className="mt-6 grid grid-cols-1 lg:grid-cols-2 gap-8">
          {/* Left: Gallery */}
          <div className="grid grid-cols-2 gap-4">
            {product.images.map((p, idx) => {
              const src = importImage(p);
              return (
                <img
                  key={idx}
                  src={src}
                  alt={`img-${idx}`}
                  className="w-full h-48 object-cover rounded-lg cursor-pointer"
                  onClick={() => {
                    // lightbox cơ bản
                    const imgWin = window.open();
                    imgWin.document.write(
                      `<img src="${src}" style="max-width:90vw;max-height:90vh;" />`
                    );
                  }}
                />
              );
            })}
          </div>

          {/* Right: Info */}
          <div className="space-y-4">
            <h2 className="text-2xl font-bold">
              {product.title} – {product.author}
            </h2>
            <div className="text-xl text-red-600 font-semibold">
              {product.price}
            </div>
            <div className="flex items-center space-x-2 text-sm">
              <span>{stars}</span>
              <span className="text-gray-500">| {product.views} view</span>
            </div>
            <p className="text-gray-600 leading-relaxed">
              {product.descriptionText.split("\n")[0]}
            </p>
            <ul className="list-disc list-inside text-gray-600 space-y-1">
              {product.bulletPoints.map((pt, i) => (
                <li key={i}>{pt}</li>
              ))}
            </ul>

            {/* Quantity & Buttons */}
            <div className="flex items-center space-x-4 mt-4">
              <div className="flex items-center border border-gray-300 rounded">
                <button
                  onClick={() => setQty((q) => Math.max(1, q - 1))}
                  className="px-3 py-1 text-lg"
                >
                  –
                </button>
                <span className="px-4">{qty}</span>
                <button
                  onClick={() => setQty((q) => q + 1)}
                  className="px-3 py-1 text-lg"
                >
                  +
                </button>
              </div>
              <button className="bg-red-600 text-white px-6 py-2 rounded hover:bg-red-700 transition">
                Add to Cart
              </button>
              <button className="border border-black px-6 py-2 rounded hover:bg-black hover:text-white transition">
                Buy Now
              </button>
            </div>

            {/* Shop Info */}
            <div className="mt-6 flex items-center justify-between p-4 border border-gray-200 rounded">
              <div className="flex items-center space-x-3">
                <div className="w-12 h-12 bg-gray-100 rounded-full" />
                <div>
                  <div className="font-semibold">{product.shop.name}</div>
                  {product.shop.verified && (
                    <div className="text-xs text-blue-600">Verified account</div>
                  )}
                  <div className="text-xs text-gray-500">
                    {product.shop.followers} Followers • {product.shop.following} Following
                  </div>
                </div>
              </div>
              <button className="bg-red-600 text-white px-4 py-1 rounded hover:bg-red-700">
                Follow
              </button>
            </div>
          </div>
        </div>

        {/* Tabs */}
        <div className="mt-12">
          <div className="flex border-b border-gray-200">
            <button
              onClick={() => setTab("description")}
              className={`px-6 py-2 ${
                tab === "description"
                  ? "border-b-2 border-black font-semibold"
                  : "text-gray-500"
              }`}
            >
              Description
            </button>
            <button
              onClick={() => setTab("reviews")}
              className={`px-6 py-2 ${
                tab === "reviews"
                  ? "border-b-2 border-black font-semibold"
                  : "text-gray-500"
              }`}
            >
              Reviews
            </button>
          </div>

          {tab === "description" && (
            <div className="prose prose-sm max-w-none mt-4 text-gray-700">
              {product.descriptionText.split("\n").map((line, i) => (
                <p key={i}>{line}</p>
              ))}
            </div>
          )}

          {tab === "reviews" && (
            <div className="mt-6 space-y-8">
              {/* Reviews List (hardcode ví dụ) */}
              {[1, 2].map((r) => (
                <div key={r} className="flex space-x-4">
                  <div className="w-10 h-10 bg-gray-200 rounded-full" />
                  <div>
                    <div className="font-semibold">User {r}</div>
                    <div className="flex space-x-1 text-yellow-400 text-sm">
                      {[...Array(5)].map((_, i) => (
                        <FaStar key={i} />
                      ))}
                    </div>
                    <p className="text-gray-600 mt-1">
                      Lorem ipsum dolor sit amet, consectetur adipiscing elit.
                    </p>
                  </div>
                </div>
              ))}

              {/* Review Form */}
              <form className="grid grid-cols-1 md:grid-cols-2 gap-6">
                <input
                  type="text"
                  placeholder="Your Name"
                  className="border border-gray-300 px-4 py-2 rounded"
                />
                <input
                  type="email"
                  placeholder="Your Email"
                  className="border border-gray-300 px-4 py-2 rounded"
                />
                <textarea
                  placeholder="Your Review"
                  className="border border-gray-300 px-4 py-2 rounded col-span-full"
                  rows={4}
                />
                <div className="flex items-center space-x-2">
                  <span>Your Rating:</span>
                  {[...Array(5)].map((_, i) => (
                    <FaRegStar key={i} className="text-gray-400" />
                  ))}
                </div>
                <button
                  type="submit"
                  className="bg-red-600 text-white px-6 py-2 rounded hover:bg-red-700 col-start-2"
                >
                  Post Review
                </button>
              </form>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default ProductDetails;
