// src/pages/ProductDetails/ProductDetails.js
import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { FaStar, FaRegStar } from "react-icons/fa";

const ProductDetails = () => {
  const { slug } = useParams();
  const [product, setProduct] = useState(null);
  const [qty, setQty] = useState(1);

  useEffect(() => {
    const titleFromSlug = slug?.replace(/[-_]/g, " ").trim() || "Có Hai Con Mèo Ngồi Bên Cửa Sổ";

    setProduct({
      title: titleFromSlug,
      author: "Nguyễn Nhật Ánh",
      price: "42.000 VND",
      rating: 5,
      views: 32,
      description: [
        "Lorem ipsum dolor sit amet consectetur adipiscing elit. Hic excepturi quibusdam odio deleniti reprehenderit facilis.",
        "Lorem ipsum dolor sit amet consectetur.",
        "Lorem ipsum dolor sit amet consectetur.",
        "Lorem ipsum dolor sit amet consectetur.",
      ],
      images: [
        "bestSellerOne.webp",
        "bestSellerTwo.webp",
        "bestSellerThree.webp",
        "bestSellerFour.webp",
      ],
    });
  }, [slug]);

  if (!product) {
    return <div className="p-8 text-center">Loading…</div>;
  }

  const { title, author, price, rating, views, description, images } = product;
  const [mainImage, ...thumbs] = images;

  const importImg = (file) => {
    try {
      return require(`../../assets/images/products/bestSeller/${file}`);
    } catch {
      return "";
    }
  };

  const stars = Array.from({ length: 5 }, (_, i) =>
    i < rating
      ? <FaStar key={i} className="inline text-yellow-400" />
      : <FaRegStar key={i} className="inline text-gray-300" />
  );

  return (
    <div className="bg-white">
      <div className="max-w-7xl mx-auto px-4 py-6">
        {/* Header */}
        <h1 className="text-4xl italic font-bold">Product</h1>
        <p className="text-sm text-gray-600 mt-1">
          /product listing/{title} – {author}
        </p>

        {/* Main layout: 2 cols on md+ */}
        <div className="mt-8 grid grid-cols-1 md:grid-cols-2 gap-8 items-start">
          {/* Left: image gallery */}
          <div className="space-y-4">
            {/* Main image */}
            <div className="bg-gray-100 rounded-lg overflow-hidden">
              <img
                src={importImg(mainImage)}
                alt="main"
                className="w-full h-auto object-cover"
              />
            </div>
            {/* Thumbnails (md+) */}
            <div className="hidden md:flex space-x-4">
              {thumbs.slice(0, 3).map((file, idx) => (
                <button
                  key={idx}
                  onClick={() => {}}
                  className="flex-none bg-gray-100 rounded-lg overflow-hidden"
                >
                  <img
                    src={importImg(file)}
                    alt={`thumb-${idx}`}
                    className="w-24 h-24 object-cover"
                  />
                </button>
              ))}
            </div>
          </div>

          {/* Right: details */}
          <div className="space-y-6">
            <h2 className="text-2xl font-semibold">{title} – {author}</h2>
            <div className="flex items-center space-x-2 text-sm">
              <span className="text-xl font-semibold text-red-600">{price}</span>
              <span className="text-gray-400">|</span>
              <span className="flex items-center space-x-1">{stars}</span>
              <span className="text-gray-500">| {views} view</span>
            </div>
            <p className="text-gray-700">{description[0]}</p>
            <ul className="list-disc list-inside space-y-1 text-gray-700">
              {description.slice(1).map((line,i) => (
                <li key={i}>{line}</li>
              ))}
            </ul>

            {/* Quantity & Add to Cart */}
            <div className="flex items-center space-x-4">
              <div className="inline-flex bg-black text-white rounded">
                <button
                  onClick={() => setQty(q => Math.max(1, q - 1))}
                  className="px-4 py-2"
                >–</button>
                <span className="px-4 flex items-center justify-center">{qty}</span>
                <button
                  onClick={() => setQty(q => q + 1)}
                  className="px-4 py-2"
                >+</button>
              </div>
              <button className="flex-1 bg-red-600 text-white px-6 py-2 rounded hover:bg-red-700 transition">
                Add to Cart
              </button>
            </div>
            {/* Buy Now */}
            <button className="w-full bg-black text-white px-6 py-2 rounded hover:bg-gray-800 transition">
              Buy Now
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductDetails;
