// src/pages/ProductDetails/ProductDetails.js
import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import Breadcrumbs from "../../components/pageProps/Breadcrumbs";
import { FaStar, FaRegStar } from "react-icons/fa";

const ProductDetails = () => {
  const location = useLocation();

  // Khởi tạo luôn có images là mảng
  const [product, setProduct] = useState({ images: [] });
  const [prevLocation, setPrevLocation] = useState("");
  const [qty, setQty] = useState(1);

  useEffect(() => {
    if (location.state?.item) {
      setProduct({
        ...location.state.item,
        images: location.state.item.images || [],
      });
    } else {
      // Hardcode sample nếu không có data từ state
      setProduct({
        name: "Có Hai Con Mèo Ngồi Bên Cửa Sổ",
        author: "Nguyễn Nhật Ánh",
        price: "42.000 VND",
        rating: 4,
        views: 32,
        description: [
          "Lorem ipsum dolor sit amet consectetur adipiscing elit. Hic excepturi quibusdam odio deleniti reprehenderit facilis.",
          "Lorem ipsum dolor sit amet consectetur.",
          "Lorem ipsum dolor sit amet consectetur.",
          "Lorem ipsum dolor sit amet consectetur.",
        ],
        images: [
          "bestSellerOne.webp",
          "bestSellerTwo.webp",
          "bestSellerThree.webp",
          "bestSellerFour.webp",
        ],
      });
    }
    setPrevLocation(location.pathname);
  }, [location]);

  // Tách main + 3 thumbnail
  const mainImage = product.images[0];
  const thumbs = product.images.slice(1, 4);

  // Helper import hình từ thư mục bestSeller
  const importImg = (fileName) => {
    try {
      return require(`../../assets/images/products/bestSeller/${fileName}`);
    } catch {
      return "";
    }
  };

  // Tạo danh sách sao
  const stars = [];
  for (let i = 1; i <= 5; i++) {
    stars.push(
      i <= (product.rating || 0) ? (
        <FaStar key={i} className="inline text-yellow-400" />
      ) : (
        <FaRegStar key={i} className="inline text-gray-300" />
      )
    );
  }

  return (
    <div className="bg-white">
      <div className="max-w-container mx-auto px-4 pt-6">
        {/* Breadcrumb */}
        <Breadcrumbs title="Product" prevLocation={prevLocation} />

        {/* Grid 3 cột */}
        <div className="mt-6 grid grid-cols-1 md:grid-cols-3 gap-6">
          {/* 1) Column thumbnails */}
          <div className="flex flex-col gap-4">
            {thumbs.map((file, i) => {
              const src = importImg(file);
              return (
                src && (
                  <img
                    key={i}
                    src={src}
                    alt={`thumb-${i}`}
                    className="w-full h-32 object-cover rounded-lg"
                  />
                )
              );
            })}
          </div>

          {/* 2) Column ảnh chính */}
          <div>
            {mainImage && (
              <img
                src={importImg(mainImage)}
                alt="main"
                className="w-full h-full object-cover rounded-lg"
              />
            )}
          </div>

          {/* 3) Column thông tin */}
          <div className="flex flex-col justify-start gap-4">
            <h2 className="text-2xl font-bold">
              {product.name} – {product.author}
            </h2>
            <div className="text-xl font-semibold text-red-600">
              {product.price}
            </div>
            <div className="flex items-center space-x-2 text-sm">
              <span>{stars}</span>
              <span className="text-gray-500">| {product.views} view</span>
            </div>
            <p className="text-gray-700">
              {product.description[0]}
            </p>
            <ul className="list-disc list-inside space-y-1 text-gray-700">
              {product.description.slice(1).map((line, i) => (
                <li key={i}>{line}</li>
              ))}
            </ul>

            {/* Quantity + Buttons */}
            <div className="flex items-center space-x-2 mt-4">
              <button
                onClick={() => setQty((q) => Math.max(1, q - 1))}
                className="w-8 h-8 bg-black text-white flex items-center justify-center rounded"
              >
                –
              </button>
              <span className="w-8 text-center">{qty}</span>
              <button
                onClick={() => setQty((q) => q + 1)}
                className="w-8 h-8 bg-black text-white flex items-center justify-center rounded"
              >
                +
              </button>
              <button className="ml-4 bg-red-600 text-white px-6 py-2 rounded hover:bg-red-700 transition">
                Add to Cart
              </button>
            </div>
            <button className="mt-2 bg-black text-white px-6 py-2 rounded hover:bg-gray-800 transition">
              Buy Now
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductDetails;
