// src/pages/ProductDetails/ProductDetails.js
import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { FaStar, FaRegStar } from "react-icons/fa";

const ProductDetails = () => {
  const { slug } = useParams();
  const [product, setProduct] = useState(null);
  const [qty, setQty] = useState(1);

  useEffect(() => {
    const titleFromSlug = slug
      ? slug.replace(/[-_]/g, " ").trim()
      : "Có Hai Con Mèo Ngồi Bên Cửa Sổ";

    setProduct({
      title: titleFromSlug,
      author: "Nguyễn Nhật Ánh",
      price: "42.000 VND",
      rating: 5,
      views: 32,
      description: [
        "Lorem ipsum dolor sit amet consectetur adipiscing elit. Hic excepturi quibusdam odio deleniti reprehenderit facilis.",
        "Lorem ipsum dolor sit amet consectetur.",
        "Lorem ipsum dolor sit amet consectetur.",
        "Lorem ipsum dolor sit amet consectetur.",
      ],
      images: [
        "bestSellerOne.webp",
        "bestSellerTwo.webp",
        "bestSellerThree.webp",
        "bestSellerFour.webp",
      ],
    });
  }, [slug]);

  if (!product) return <div className="p-8 text-center">Loading...</div>;

  const { title, author, price, rating, views, description, images } = product;
  const [mainImage, ...thumbs] = images;

  // build stars
  const stars = Array.from({ length: 5 }, (_, i) =>
    i < rating ? (
      <FaStar key={i} className="inline text-yellow-400" />
    ) : (
      <FaRegStar key={i} className="inline text-gray-300" />
    )
  );

  const importImg = (f) => {
    try {
      return require(`../../assets/images/products/bestSeller/${f}`);
    } catch {
      return "";
    }
  };

  return (
    <div className="bg-white">
      <div className="max-w-container mx-auto px-4 pt-6">
        {/* Header */}
        <h1 className="text-4xl italic font-bold">Product</h1>
        <p className="text-sm text-gray-600 mt-1">
          /product listing/{title} – {author}
        </p>

        {/* 3-col grid: thumbs 1/5, main 1/5 (20%), info 3/5 */}
        <div className="mt-8 grid grid-cols-1 md:grid-cols-5 gap-6">
          {/* thumbnails (1/5) */}
          <div className="hidden md:grid grid-rows-3 gap-4 md:col-span-1">
            {thumbs.slice(0, 3).map((f, i) => {
              const src = importImg(f);
              return (
                src && (
                  <img
                    key={i}
                    src={src}
                    alt={`thumb-${i}`}
                    className="w-full h-32 object-cover rounded-lg"
                  />
                )
              );
            })}
          </div>

          {/* main image (1/5 ≈ 20%) */}
          <div className="md:col-span-1 bg-gray-100 rounded-lg overflow-hidden flex items-center justify-center">
            {mainImage ? (
              <img
                src={importImg(mainImage)}
                alt="main"
                className="w-full h-full object-cover"
              />
            ) : (
              <div className="text-gray-500 p-8 text-center">No image</div>
            )}
          </div>

          {/* info (3/5) */}
          <div className="md:col-span-3 flex flex-col justify-start gap-6">
            <h2 className="text-2xl font-semibold">
              {title} – {author}
            </h2>
            <div className="flex items-center space-x-2 text-sm">
              <span className="text-xl font-semibold text-red-600">
                {price}
              </span>
              <span className="text-gray-400">|</span>
              <span className="flex items-center space-x-1">{stars}</span>
              <span className="text-gray-500">| {views} view</span>
            </div>
            <p className="text-gray-700">{description[0]}</p>
            <ul className="list-disc list-inside space-y-1 text-gray-700">
              {description.slice(1).map((line, i) => (
                <li key={i}>{line}</li>
              ))}
            </ul>
            <div className="flex items-center space-x-2">
              <button
                onClick={() => setQty((q) => Math.max(1, q - 1))}
                className="w-8 h-8 bg-black text-white flex items-center justify-center rounded"
              >
                –
              </button>
              <span className="w-8 text-center">{qty}</span>
              <button
                onClick={() => setQty((q) => q + 1)}
                className="w-8 h-8 bg-black text-white flex items-center justify-center rounded"
              >
                +
              </button>
              <button className="ml-4 bg-red-600 text-white px-6 py-2 rounded hover:bg-red-700 transition">
                Add to Cart
              </button>
            </div>
            <button className="w-full bg-black text-white px-6 py-2 rounded hover:bg-gray-800 transition">
              Buy Now
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductDetails;
