// src/pages/ProductDetails/ProductDetails.js
import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import Breadcrumbs from "../../components/pageProps/Breadcrumbs";
import { FaStar, FaRegStar } from "react-icons/fa";

const ProductDetails = () => {
  const { state } = useLocation();

  // 1) Khởi tạo product null ban đầu
  const [product, setProduct] = useState(null);
  const [prevLocation, setPrevLocation] = useState("");
  const [qty, setQty] = useState(1);

  useEffect(() => {
    // Nếu gọi từ shop page có truyền state.item
    if (state?.item) {
      const item = state.item;
      setProduct({
        // ưu tiên title, fallback sang name nếu có
        title: item.title || item.name || "",
        author: item.author || item.writer || "",
        price: item.price?.toString() || "",
        rating: typeof item.rating === "number" ? item.rating : 0,
        views: typeof item.views === "number" ? item.views : 0,
        description: Array.isArray(item.description) ? item.description : [],
        images: Array.isArray(item.images) ? item.images : [],
      });
    } else {
      // Hardcode sample khi test trực tiếp
      setProduct({
        title: "Có Hai Con Mèo Ngồi Bên Cửa Sổ",
        author: "Nguyễn Nhật Ánh",
        price: "60.00",
        rating: 0,
        views: 0,
        description: [
          "Lorem ipsum dolor sit amet consectetur adipiscing elit. Hic excepturi quibusdam odio deleniti reprehenderit facilis.",
          "Lorem ipsum dolor sit amet consectetur.",
          "Lorem ipsum dolor sit amet consectetur.",
          "Lorem ipsum dolor sit amet consectetur.",
        ],
        images: [
          "bestSellerOne.webp",
          "bestSellerTwo.webp",
          "bestSellerThree.webp",
          "bestSellerFour.webp",
        ],
      });
    }
    setPrevLocation(window.location.pathname);
  }, [state]);

  // 2) Guard loading
  if (!product) {
    return <div className="p-8 text-center">Loading...</div>;
  }

  // 3) Chuẩn hóa arrays
  const images = product.images;
  const descArr = product.description;

  // 4) Main image và 3 thumbnails
  const mainImage = images[0] || null;
  const thumbs = images.slice(1, 4);

  // 5) Helper import ảnh
  const importImg = (fileName) => {
    try {
      return require(`../../assets/images/products/bestSeller/${fileName}`);
    } catch {
      return "";
    }
  };

  // 6) Build stars
  const stars = [];
  for (let i = 1; i <= 5; i++) {
    stars.push(
      i <= product.rating ? (
        <FaStar key={i} className="inline text-yellow-400" />
      ) : (
        <FaRegStar key={i} className="inline text-gray-300" />
      )
    );
  }

  return (
    <div className="bg-white">
      <div className="max-w-container mx-auto px-4 pt-6">

        {/* Breadcrumb */}
        <Breadcrumbs title="Product" prevLocation={prevLocation} />

        {/* Tiêu đề & đường dẫn giống mockup */}
        <div className="mt-4">
          <h1 className="text-3xl italic font-bold">{product.title}</h1>
          <p className="text-sm text-gray-600 mt-1">
            /product listing/{product.title} – {product.author}
          </p>
        </div>

        {/* Layout 3 cột */}
        <div className="mt-6 grid grid-cols-1 md:grid-cols-3 gap-6">
          {/* 1) Thumbnails */}
          <div className="flex flex-col gap-4">
            {thumbs.length > 0 ? (
              thumbs.map((f, i) => {
                const src = importImg(f);
                return (
                  src && (
                    <img
                      key={i}
                      src={src}
                      alt={`thumb-${i}`}
                      className="w-full h-32 object-cover rounded-lg"
                    />
                  )
                );
              })
            ) : (
              <div className="text-gray-500 text-center">No thumbnails</div>
            )}
          </div>

          {/* 2) Main image */}
          <div className="flex items-center justify-center bg-gray-100 rounded-lg overflow-hidden">
            {mainImage ? (
              <img
                src={importImg(mainImage)}
                alt="main"
                className="w-full h-full object-cover"
              />
            ) : (
              <div className="text-gray-500">No image</div>
            )}
          </div>

          {/* 3) Thông tin sản phẩm */}
          <div className="flex flex-col justify-start gap-4">
            <h2 className="text-2xl font-bold">
              {product.title} – {product.author}
            </h2>
            <div className="text-xl font-semibold text-red-600">
              {product.price}
            </div>
            <div className="flex items-center space-x-2 text-sm">
              <span>{stars}</span>
              <span className="text-gray-500">| {product.views} view</span>
            </div>

            {/* Mô tả */}
            {descArr.length > 0 && (
              <>
                <p className="text-gray-700">{descArr[0]}</p>
                <ul className="list-disc list-inside space-y-1 text-gray-700">
                  {descArr.slice(1).map((line, idx) => (
                    <li key={idx}>{line}</li>
                  ))}
                </ul>
              </>
            )}

            {/* Số lượng & Buttons */}
            <div className="flex items-center space-x-2 mt-4">
              <button
                onClick={() => setQty((q) => Math.max(1, q - 1))}
                className="w-8 h-8 bg-black text-white flex items-center justify-center rounded"
              >
                –
              </button>
              <span className="w-8 text-center">{qty}</span>
              <button
                onClick={() => setQty((q) => q + 1)}
                className="w-8 h-8 bg-black text-white flex items-center justify-center rounded"
              >
                +
              </button>
              <button className="ml-4 bg-red-600 text-white px-6 py-2 rounded hover:bg-red-700 transition">
                Add to Cart
              </button>
            </div>
            <button className="mt-2 bg-black text-white px-6 py-2 rounded hover:bg-gray-800 transition">
              Buy Now
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductDetails;
